$text = file_get_contents('../SouthAmerica.svg');
$lines = explode("\n", $text);
$output = '';
$g = false;
$path = false;
foreach ($lines as $line){
  $output .= $line . "\n";
  if (strpos($line, '<g') !== false){
    $g = true;
  }
  elseif (strpos($line, '</g') !== false) {
       $g = false;
  }
  elseif (strpos($line, '<path') !== false){
    $path = true;
  }
  elseif (strpos($line, '</path') !== false){
    $path = false;
  }
  elseif (strpos($line, 'id="') !== false){
    if (($g == true && $path == false) || ($g == false && $path == true)){
      $id = findId($line);
      if ($id){
        $country = strtoupper($id);
        //$output .=  '      onclick = "navigateToCountry(\'' . $country . '\')"';
        $output .='     @click="navigateToCountry(\''. $country . '\')"';
        $output .= "\n";
      }
    }
    $path = false; // path may not end with </path.
  }
}
print_r($output);


function findId($line){
  $pos_start = strpos($line, 'id="') + 4;
  $pos_end = strpos($line, '"', $pos_start);
  $str_len = $pos_end - $pos_start;
  $id = substr($line, $pos_start, $str_len );
  if (strpos($id, 'path') !== false){
    return null;
  }
  if (strpos($id, 'title') !== false){
    return null;
  }
  return $id;
}
